CREATE TABLE fishspecies (_id integer primary key, name text, description text, baits text, method text, wikilink text);
CREATE TABLE fishcaptures (_id integer primary key autoincrement, title text, species integer not null, photo text, pounds integer, ounces integer, drams integer, comment text, FOREIGN KEY(species) REFERENCES fishspecies(_id));
CREATE TABLE fishtargets (_id integer primary key autoincrement, species integer not null, pounds integer, ounces integer, drams integer, FOREIGN KEY(species) REFERENCES fishspecies(_id));